mapper.select:
  	select     from oam_device


  			and id = #{id}


  			and dept_id = #{deptId}


  			and serial_no like concat('%',#{serialNo},'%')


  			and fixed_no like concat('%',#{fixedNo},'%')


  			and name like concat('%',#{name},'%')


  			and manufacturer like concat('%',#{manufacturer},'%')


  			and model  like concat('%',#{model},'%')


  			and buy_time = #{buyTime}


  			and over_time = #{overTime}


  			and use_contition = #{useContition}


  			and use_type = #{useType}


  			and type = #{type}


  			and type in 
			            #{v,jdbcType=VARCHAR}
