mapper.delete:

delete from sys_dict_entry
where dict_type = #{dictType,jdbcType=VARCHAR}
  and dict_code = #{dictCode,jdbcType=VARCHAR}

mapper.update:

update sys_dict_entry
set dict_name = #{dictName,jdbcType=VARCHAR},
  dict_order = #{dictOrder,jdbcType=INTEGER},
  remarks = #{remarks,jdbcType=VARCHAR}
where dict_type = #{dictType,jdbcType=VARCHAR}
  and dict_code = #{dictCode,jdbcType=VARCHAR}

mapper.select:

select dict_type, dict_code, dict_name, dict_order, remarks
from sys_dict_entry
where dict_type = #{dictType,jdbcType=VARCHAR}
  and dict_code = #{dictCode,jdbcType=VARCHAR}

select dict_type, dict_code, dict_name, dict_order, remarks from sys_dict_entry
AND dict_type = #{dictType}
AND dict_code = #{dictCode}
AND dict_name like CONCAT('%',#{dictName},'%')
AND remarks like CONCAT('%',#{remarks},'%')
order by dict_order

select dict_type, dict_code, dict_name, dict_order, remarks from sys_dict_entry
AND dict_type = #{dictType}
AND dict_code = #{dictCode}
AND dict_name like CONCAT('%',#{dictName},'%')
AND remarks like CONCAT('%',#{remarks},'%')
order by dict_order
