mapper.select:
select t1.id, t1.parent_id, t1.menu_name, t1.menu_url, t1.menu_order, t1.menu_icon,t1.isparent,t2.menu_name parent_name
from sys_menu t1 left join sys_menu t2 on t1.parent_id = t2.id   


		and t1.id=#{id}


		and t1.parent_id=#{parentId}


		and	t1.menu_name like concat('%',#{menuName},'%') 


		and t1.menu_url like concat('%',#{menuUrl},'%') 


		and t1.isparent=#{isparent} 


	order by t1.menu_order

select t1.id, t1.parent_id, t1.menu_name, t1.menu_url, t1.menu_order, t1.menu_icon,t1.isparent,t2.menu_name parent_name
from sys_menu t1 left join sys_menu t2 on t1.parent_id = t2.id   


		and t1.id=#{id}


		and t1.parent_id=#{parentId}


		and	t1.menu_name like concat('%',#{menuName},'%') 


		and t1.menu_url like concat('%',#{menuUrl},'%') 


		and t1.isparent=#{isparent} 


	order by t1.menu_order

  	 select t1.* from sys_menu t1 where   exists (
  	 	select 1 from sys_privilege t2 where  t1.id = t2.button_id and t2. button='m'

    		and ((t2.masteru='r' and t2.masteru_id=#{roleId}) or (t2.masteru='u' and t2.masteru_id=#{userId}))


    		and ((t2.masteru='r' and t2.masteru_id in

			            #{r}

    		 ) or (t2.masteru='u' and t2.masteru_id=#{userId}))

  	 )  order by parent_id,menu_order
